class Solution:
    def longestPalindrome(self, s: str) -> str:
        if len(s) < 2:
            return s

        already_check_pair = [[False for i in range(len(s))] for j in range(len(s))]
        longest_len = 1
        a, b = 0, 0

        for l in range(len(s)):
            for i in range(len(s) - l):
                if s[i] == s[i+l]:
                    if l == 0 or l == 1 or already_check_pair[i+1][i+l-1]:
                        already_check_pair[i][i+l] = True
                        if longest_len < l + 1:
                            longest_len = l + 1
                            a = i
                            b = i+l
                        continue
                already_check_pair[i][i+l] = False
        return s[a:b+1]


    def benchmark(self):
        cases = [
            ["babad", "bab"],
            ["cbbd", "bb"],
            ["a", "a"],
            ["", ""]
        ]
        for case in cases:
            yield case


if __name__=="__main__":
    sol = Solution()
    for s, gt in sol.benchmark():
        print("-----------")
        print(s)
        print(gt)
        res = sol.longestPalindrome(s)
        assert res == gt, f"{res=}, {gt=}"
